CREATE TABLE `flynx_loans`.`users` (`userID` INT(11) NOT NULL AUTO_INCREMENT , `Title` VARCHAR(20) NOT NULL , `First_name` VARCHAR(200) NOT NULL , `Middle_name` VARCHAR(100) NOT NULL , `Last_name` VARCHAR(200) NOT NULL , `Is_active` INT(11) NOT NULL DEFAULT '0' , `Created_on` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `Updated_on` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `Date_of_birth` DATE NOT NULL , `Email` VARCHAR(250) NOT NULL , `Password` VARCHAR(300) NOT NULL , `Employment_status` INT(11) NOT NULL , `Annual_Income` BIGINT NOT NULL , `Total_expenses` BIGINT NOT NULL , `Employer_name` VARCHAR(100) NOT NULL , `Confirm_terms_and_conditions` INT(11) NOT NULL DEFAULT '0' , `User_type` INT(11) NOT NULL DEFAULT '1' , `Address` INT(11) NOT NULL , `profile_picture` VARCHAR(200) NULL DEFAULT NULL , `username` VARCHAR(250) NOT NULL , `Full_name` VARCHAR(300) NOT NULL , PRIMARY KEY (`userID`)) ENGINE = InnoDB;

ALTER TABLE `users` ADD CONSTRAINT `User_type` FOREIGN KEY (`User_type`) REFERENCES `user_type`(`Ut_id`) ON DELETE CASCADE ON UPDATE CASCADE; ALTER TABLE `users` ADD CONSTRAINT `User_address` FOREIGN KEY (`Address`) REFERENCES `address`(`Address_id`) ON DELETE CASCADE ON UPDATE CASCADE; ALTER TABLE `users` ADD CONSTRAINT `User_employment_status` FOREIGN KEY (`Employment_status`) REFERENCES `employment_status`(`Employment_status_id`) ON DELETE CASCADE ON UPDATE CASCADE;